package org.acme.employee;

import javax.ejb.CreateException;
import javax.ejb.EJBException;
import javax.ejb.EntityBean;
import javax.ejb.EntityContext;
import javax.ejb.RemoveException;

public class EmployeeBean implements EntityBean {
	public int id;
	public String firstname;
	public String lastname;
	public String email;
	public EntityContext context;

	public EmployeeBean() {}

	public Integer ejbCreate(String firstname, String lastname, String email)
		throws CreateException {
		this.firstname = firstname;
		this.lastname = lastname;
		this.email = email;
		return null;
	}

	public void ejbPostCreate(String firstname, String lastname, String email)
		throws CreateException {}

	public void says(String message) {
		System.out.println(
			"[" + firstname + " " + lastname + "(" + id + ") " + 
			email + "] " + message);
	}

	public void setEntityContext(EntityContext context) {
		this.context = context;
	}

	public void unsetEntityContext() {}

	public void ejbActivate() {}

	public void ejbLoad() {}

	public void ejbPassivate() {}

	public void ejbRemove() throws RemoveException {}

	public void ejbStore() {}
}

package org.acme.employee;

import java.rmi.RemoteException;

import javax.ejb.CreateException;
import javax.ejb.EJBHome;
import javax.ejb.FinderException;

public interface EmployeeHome extends EJBHome {
    public EmployeeObject create(String firstName, String lastName, String email)
        throws RemoteException, CreateException;

    public EmployeeObject findByPrimaryKey(Integer id) 
        throws RemoteException, FinderException;
}

package org.acme.employee;

import java.rmi.RemoteException;

import javax.ejb.EJBObject;

public interface EmployeeObject extends EJBObject {
    public void says(String message) throws RemoteException;
}

